export const imageTypeValidator = control => {
  const file = control.value;
  if (!file) return {
    invalidFileType: true
  };

  switch (file.type) {
    case 'image/png':
    case 'image/jpg':
    case 'image/jpeg':
      return null;
      break;

    default:
      return {
        invalidFileType: true
      };
  }
};
